import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.css';

function RandomPhotos() {
  const [photos, setPhotos] = useState([]);
  const [isGrayscale, setIsGrayscale] = useState(false);
  const [visiblePhotos, setVisiblePhotos] = useState(4);

  useEffect(() => {
    // fetch data from API
    async function fetchData() {
      const response = await fetch('https://picsum.photos/v2/list');
      const data = await response.json();
      setPhotos(data);
    }
    fetchData();
  }, []);

  // function fetch new 4 photo inside the button 
  const handleFetchNewPhotos = async () => {
    const response = await fetch('https://picsum.photos/v2/list');
    const data = await response.json();
    setPhotos(data);
  };

  // add 4 photo in the page randome 
  const handleLoadMorePhotos = () => {
    setVisiblePhotos(visiblePhotos + 4);
  };

  return (
    <>
    <div>
      <div className="mb-10">
      <label htmlFor="grayscale-toggle">Grayscale: </label>
      <input
        type="checkbox"
        id="grayscale-toggle"
        checked={isGrayscale}
        onChange={() => setIsGrayscale(!isGrayscale)}
      />
      <button className='' onClick={handleFetchNewPhotos}>Fetch New Photos</button>
      </div>
      <div className="mt-12">
       <div className=''>
        {photos
        .sort(() => 0.5 - Math.random())
        .slice(0, visiblePhotos)
        .map( photo => (  
          <img
            className={{grid:'col-12' }}
            key={photo.id}
            src={`https://picsum.photos/id/${photo.id}/300/300`}
            alt={photo.author}
            style={{ filter: isGrayscale ? 'grayscale(100%)' : 'none' }}
          /> 

        ))}</div>
         </div> 
         <div className='mt-12'>
      <button  onClick={handleLoadMorePhotos}>Load More Photos</button> 
      </div>
    </div>
    </>
  );
}

export default RandomPhotos;
