import React, { useState, useEffect } from 'react'
import 'bootstrap/dist/css/bootstrap.css'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'


function RandomPhotos() {
  const [photos, setPhotos] = useState([]);
  const [isGrayscale, setIsGrayscale] = useState(false);
  const [visiblePhotos, setVisiblePhotos] = useState(4);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    // fetch data from API
    async function fetchData() {
      setIsLoading(true);
      const response = await fetch('https://picsum.photos/v2/list');
      const data = await response.json();
      setPhotos(data);
      setIsLoading(false);
    }
    fetchData();
  }, []);

  // function fetch new 4 photo inside the button 
  const handleFetchNewPhotos = async () => {
    setIsLoading(true);
    const response = await fetch('https://picsum.photos/v2/list');
    const data = await response.json();
    setPhotos(data);
    setIsLoading(false);
  };

  // add 4 photo in the page randome 
  const handleLoadMorePhotos = () => {
    setVisiblePhotos(visiblePhotos + 4);
  };

  return (
    <>
    <div>
      <div style={{marginBottom:20}}>
      <label htmlFor="grayscale-toggle">Grayscale: </label>
      <input
        type="checkbox"
        id="grayscale-toggle"
        checked={isGrayscale}
        onChange={() => setIsGrayscale(!isGrayscale)}
      />
     <Button style={{marginLeft:50}} variant="info" onClick={handleFetchNewPhotos}>Fetch New Photos</Button>
      </div>
      <Container>
         <Row>     
       {isLoading ? (
        <div>Loading...</div>
      ) : (
        photos
        .sort(() => 0.5 - Math.random())
        .slice(0, visiblePhotos)
        .map(photo => (
          <Col>
          <Card style={{ width: '20rem', backGround:'yellow'  }}>
          <Card.Img  alt={photo.author} key={photo.id} variant="top" src={`https://picsum.photos/id/${photo.id}/300/300`} style={{ filter: isGrayscale ? 'grayscale(100%)' : 'none' }} />
          <Card.Body>
            <Card.Title style={{ color: 'black'  }}>{photo.author}</Card.Title>
            <Card.Text style={{ color: 'black'  }}>   
            {`https://picsum.photos/id/${photo.id}/300/300`}
            </Card.Text>     
          </Card.Body>
        </Card>    
        </Col>     

          ))
          )}   
     </Row>
     </Container>

         <div style={{marginTop:20}}>
      <Button  className="primary"  onClick={handleLoadMorePhotos}>Load More Photos</Button> 
      </div>
    </div>
    </>
  );
}

export default RandomPhotos;
