import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.css';

function RandomPhotos() {
  const [photos, setPhotos] = useState([]);
  const [isGrayscale, setIsGrayscale] = useState(false);
  const [visiblePhotos, setVisiblePhotos] = useState(4);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    // fetch data from API
    async function fetchData() {
      setIsLoading(true);
      const response = await fetch('https://picsum.photos/v2/list');
      const data = await response.json();
      setPhotos(data);
      setIsLoading(false);
    }
    fetchData();
  }, []);

  // function fetch new 4 photo inside the button 
  const handleFetchNewPhotos = async () => {
    setIsLoading(true);
    const response = await fetch('https://picsum.photos/v2/list');
    const data = await response.json();
    setPhotos(data);
    setIsLoading(false);
  };

  // add 4 photo in the page randome 
  const handleLoadMorePhotos = () => {
    setVisiblePhotos(visiblePhotos + 4);
  };

  return (
    <>
    <div>
      <div className="mb-10">
      <label htmlFor="grayscale-toggle">Grayscale: </label>
      <input
        type="checkbox"
        id="grayscale-toggle"
        checked={isGrayscale}
        onChange={() => setIsGrayscale(!isGrayscale)}
      />
      <button className="btn btn-info"  onClick={handleFetchNewPhotos}>Fetch New Photos</button>
      </div>
      <div className="container">
      <div class="row">
         <div class="col">
       {isLoading ? (
        <div>Loading...</div>
      ) : (
        photos
        .sort(() => 0.5 - Math.random())
        .slice(0, visiblePhotos)
        .map(photo => (         
          <img
            className={{grid:'col-6' }}
            key={photo.id}
            src={`https://picsum.photos/id/${photo.id}/300/300`}
            alt={photo.author}
            style={{ filter: isGrayscale ? 'grayscale(100%)' : 'none' }}
          />     
          ))
          )}   </div>
        </div> 
         </div> 
         <div className=''>
      <button  className="btn btn-primary"  onClick={handleLoadMorePhotos}>Load More Photos</button> 
      </div>
    </div>
    </>
  );
}

export default RandomPhotos;
